def func_write (payment,element,name,RadarValue)
{
	int totalOfValuesChecked = Integer.parseInt(context.expand( '${#TestCase#TotalOfValuesChecked}' ))
	def prodType = context.expand( '${Renewal686#PRODUCT_TYPE}' )

	if (context.expand('${Renewal686#BRC}') == "Y")
	{
		Car1 = context.expand( '${Renewal686#Car1}' )
		Car2 = context.expand( '${Renewal686#Car2}' )
		Car3 = context.expand( '${Renewal686#Car3}' )
		Car4 = context.expand( '${Renewal686#Car4}' )
		if (Car4 != "")
		{
			product_type = prodType + ",BRC(" + "Car1:" + Car1 + ",Car2:" + Car2 + ",Car3:" + Car3 + ",Car4:" + Car4 + ")"
		}
		else if (Car3 != "")
		{
			product_type = prodType + ",BRC(" + "Car1:" + Car1 + ",Car2:" + Car2 + ",Car3:" + Car3 + ")"
		}
		else if (Car2 != "")
		{
			product_type = prodType + ",BRC("  + "Car1:" + Car1 + ",Car2:" + Car2 + ")"
		}
		else
		{
			product_type = prodType + ",BRC(" + "Car1:" + Car1 + ")"
		}
	}
	else
	{
		product_type = context.expand( '${Renewal686#PRODUCT_TYPE}' )
	}

	
	log.info Pass=(context.expand('${Renewal686#ID}') + "|" + context.expand( '${Renewal686#OFFER_CODE}' ) + "|"  + payment + "|" + product_type + "|" + context.expand( '${Renewal686#COVER}' ) + "|" + name + "|" + element + "| " + RadarValue )

	BufferedWriter bw = new BufferedWriter (new FileWriter (context.expand('${#TestCase#File_YR1}'), true));
	bw.write (Pass);
	bw.newLine();
	bw.flush();
	bw.close();
	totalOfValuesChecked++
	testRunner.testCase.setPropertyValue( 'totalOfValuesChecked',  new Integer(totalOfValuesChecked).toString())
}

